import React, { useState } from "react";
// import { FAB } from "react-native-elements";

import Background from "../components/Background";
import BackButton from "../components/BackButton";
import {
  Image,
  Pressable,
  ScrollView,
  StyleSheet,
  Text,
  TextInput,
  TouchableOpacity,
  View,
  Modal,
  // Pressable,
} from "react-native";
import DropDownPicker from "react-native-dropdown-picker";
import { theme } from "../core/theme";
const myHouses = [
  {
    key: "1",
    Id: "13256",
    Name: "optional",
    MainImage: "../assets/avatar.png",
    Desciption: {
      Location: [31.32157, 34.62123],
      Rooms: 5,
      Floors: 2,
      Kitchens: 1,
      Bathrooms: 3,
      BedRooms: 3,
      LivingRooms: 2,
    },
    Images: [{ Src: "image.jpg", Title: "optional" }],
  },
  {
    key: "2",
    Id: "13256",
    Name: "optional",
    MainImage: "img.jpg",
    Desciption: {
      Location: [31.32157, 34.62123],
      Rooms: 5,
      Floors: 2,
      Kitchens: 1,
      Bathrooms: 3,
      BedRooms: 3,
      LivingRooms: 2,
    },
    Images: [{ Src: "image.jpg", Title: "optional" }],
  },
  {
    key: "3",
    Id: "13256",
    Name: "optional",
    MainImage: "img.jpg",
    Desciption: {
      Location: [31.32157, 34.62123],
      Rooms: 5,
      Floors: 2,
      Kitchens: 1,
      Bathrooms: 3,
      BedRooms: 3,
      LivingRooms: 2,
    },
    Images: [{ Src: "image.jpg", Title: "optional" }],
  },
  {
    key: "4",
    Id: "13256",
    Name: "optional",
    MainImage: "img.jpg",
    Desciption: {
      Location: [31.32157, 34.62123],
      Rooms: 5,
      Floors: 2,
      Kitchens: 1,
      Bathrooms: 3,
      BedRooms: 3,
      LivingRooms: 2,
    },
    Images: [{ Src: "image.jpg", Title: "optional" }],
  },
  {
    key: "5",
    Id: "13256",
    Name: "optional",
    MainImage: "img.jpg",
    Desciption: {
      Location: [31.32157, 34.62123],
      Rooms: 5,
      Floors: 2,
      Kitchens: 1,
      Bathrooms: 3,
      BedRooms: 3,
      LivingRooms: 2,
    },
    Images: [{ Src: "image.jpg", Title: "optional" }],
  },
  {
    key: "6",
    Id: "13256",
    Name: "optional",
    MainImage: "img.jpg",
    Desciption: {
      Location: [31.32157, 34.62123],
      Rooms: 5,
      Floors: 2,
      Kitchens: 1,
      Bathrooms: 3,
      BedRooms: 3,
      LivingRooms: 2,
    },
    Images: [{ Src: "image.jpg", Title: "optional" }],
  },
];
export default function AddMyApartment({ navigation }) {
  const [ChangedData, SetChangedData] = React.useState([]);

  const [open, setOpen] = useState(false);
  const [value, setValue] = useState(null);
  const [items, setItems] = useState([
    { label: "Apartment", value: 1 },
    { label: "House", value: 2 },
  ]);
  const [modalData, setModalData] = useState({
    key: "5",
    Id: "13256",
    Name: "optional",
    MainImage: "img.jpg",
    Desciption: {
      Location: [31.32157, 34.62123],
      Rooms: 5,
      Floors: 2,
      Kitchens: 1,
      Bathrooms: 3,
      BedRooms: 3,
      LivingRooms: 2,
    },
    Images: [{ Src: "image.jpg", Title: "optional" }],
  });
  const [modalVisible, setModalVisible] = useState(false);
  // SetChangedData(() => [...myHouses]);
  // let houseType = [];
  // houseType.push({ label: "Apartment", value: 1 });
  // setItems(houseType);
  // React.useEffect(() => {})
  const write = (title, toEdit, data) => {
    console.log("data", data);
    return (
      <View style={styles.write}>
        <Text style={styles.writeText}>{title}</Text>
        {toEdit && <TextInput style={styles.writeInput}></TextInput>}
        {!toEdit && (
          <TextInput style={styles.writeInput} defaultValue={data}></TextInput>
        )}
        <Text>{data}</Text>
      </View>
    );
  };
  const houseCard = (house) => {
    return (
      <TouchableOpacity
        style={styles.houseCardView}
        key={house.key}
        activeOpacity={0.7}
        onLongPress={() => {
          setModalData(house);
          setModalVisible(!modalVisible);
          console.log(modalData);
        }}
      >
        <View style={styles.houseCardImageView}>
          <Image
            style={styles.houseCardMainImage}
            source={require("..//assets/house2.jpg")}
          />
        </View>
        <View style={styles.houseCardDescriptionView}>
          <Text style={{ fontSize: 25 }}>{house.Name}</Text>
        </View>
      </TouchableOpacity>
    );
  };
  const listAll = () => {
    let output = [];
    let counter = 0;
    myHouses.map((element) => {
      output[counter] = houseCard(element);
      counter++;
    });
    return output;
  };
  const Form = () => {
    return (
      <View>
        {/* <DropDownPicker
          style={{
            borderColor: "#1c6669",
            borderBottomWidth: 2,
          }}
          textStyle={{
            fontSize: 15,
          }}
          showTickIcon={false}
          placeholder="House Type"
          containerStyle={styles.dropDownStyle}
          open={open}
          value={value}
          items={items}
          setOpen={setOpen}
          setValue={setValue}
          setItems={setItems}
        /> */}
        {/* {write("Rooms")}
        {write("Floors")}
        {write("Kitchens")}
        {write("Bathrooms")}
        {write("Rooms")}
        {write("Floors")}
        {write("Kitchens")}
        {write("Bathrooms")}
        {write("Rooms")}
        {write("Floors")}
        {write("Kitchens")}
        {write("Bathrooms")}
        {write("Rooms")}
        {write("Floors")}
        {write("Kitchens")}
        {write("Bathrooms")} */}
      </View>
    );
  };
  return (
    <Background style={{ marginTop: 15 }}>
      <BackButton goBack={navigation.goBack} />
      <Modal
        animationType="slide"
        transparent={true}
        visible={modalVisible}
        onRequestClose={() => {
          Alert.alert("Modal has been closed.");
          setModalVisible(!modalVisible);
        }}
      >
        <View style={styles.centeredView}>
          <View style={styles.modalView}>
            <ScrollView style={styles.ScrollView1}>
              <Text style={styles.modalText}>
                Hello World! {JSON.stringify(modalData)}
              </Text>
              {write("Rooms", true, modalData.Desciption.Rooms)}
              {write("Floors", true, modalData.Desciption.Floors)}
              {write("Kitchens", true, modalData.Desciption.Kitchens)}
              {write("Bathrooms", true, modalData.Desciption.Bathrooms)}

              <Pressable
                style={[styles.button, styles.buttonClose]}
                onPress={() => {
                  setModalVisible(!modalVisible);
                }}
              >
                <Text style={styles.textStyle}>Hide Modal</Text>
              </Pressable>
            </ScrollView>
          </View>
        </View>
      </Modal>
      <TouchableOpacity activeOpacity={0.7} style={styles.plusSignOpView}>
        <Image
          //We are making FAB using TouchableOpacity with an image
          //We are using online image here
          source={{
            uri: "https://raw.githubusercontent.com/AboutReact/sampleresource/master/plus_icon.png",
          }}
          //You can use you project image Example below
          //source={require('./images/float-add-icon.png')}
          style={styles.floatingButtonStyle}
        />
      </TouchableOpacity>
      <ScrollView
        style={styles.ScrollView1}
        contentContainerStyle={{ flexGrow: 1, justifyContent: "center" }}
      >
        <View style={styles.View2}>{listAll()}</View>
      </ScrollView>
    </Background>
  );
}
const Cat = (hehe) => {
  return <Text>Hello, I am tozz! + {hehe} </Text>;
};
const styles = StyleSheet.create({
  ScrollView1: {
    height: "100%",
    width: "100%",
  },
  View2: {
    alignItems: "center",
    justifyContent: "center",
    alignContent: "space-around",
    flex: 1,
  },
  dropDownStyle: {
    // borderTopWidth: 2,
    marginTop: 4,
    width: "95%",
    alignSelf: "center",
  },
  DropContainer: {
    width: "100%",
    borderTopWidth: 2,
  },
  write: {
    width: "100%",
    flexDirection: "row",
    height: 50,
    padding: 5,
  },
  writeText: {
    // textAlign: "center",
    flex: 1,
  },
  writeInput: {
    flex: 1,
    borderWidth: 1,
    borderColor: theme.colors.secondary,
  },
  houseCardView: {
    width: "100%",
    minHeight: 150,
    borderWidth: 2,
    margin: 2,
  },
  houseCardImageView: {
    width: "100%",
    flex: 1,
  },
  houseCardDescriptionView: {
    width: "100%",
    // alignSelf: "center",
    alignItems: "center",
    // flex: 1,
    // maxHeight: 50,
    backgroundColor: "white",
    opacity: 90,
    // position: "relative",
    // height: "100%",
  },
  houseCardMainImage: {
    width: "100%",
    height: "100%",
    // resizeMode: "contain",
  },
  floatingButtonStyle: {
    resizeMode: "contain",
    width: 50,
    height: 50,
  },
  plusSignOpView: {
    position: "absolute",
    width: 50,
    height: 50,
    alignItems: "center",
    justifyContent: "center",
    right: 30,
    bottom: 30,
    zIndex: 1,
  },
  modalView: {
    margin: 20,
    backgroundColor: "white",
    borderRadius: 20,
    padding: 35,
    alignItems: "center",
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 4,
    elevation: 5,
  },
  button: {
    borderRadius: 20,
    padding: 10,
    elevation: 2,
  },
  buttonOpen: {
    backgroundColor: "#F194FF",
  },
  buttonClose: {
    backgroundColor: "#2196F3",
  },
  textStyle: {
    color: "white",
    fontWeight: "bold",
    textAlign: "center",
  },
  modalText: {
    marginBottom: 15,
    textAlign: "center",
  },
});
